var result = 0;
if (vars.$G_kmsearchengine == null && vars.$L_file.searchservername == null)
{
      system.library.KMUtils.domessageBox(system.functions.scmsg(247, "km"));
}

if (vars.$G_kmsearchengine === 'IDOL')
{
    result = lib.KMCollection_Update_IDOL.require().reindex();
    
    if ( result == RC_SUCCESS){
        if (vars['$L.file']['kbtype'] === 'sclib'){
            if (vars.$L_file.indexstatus == lib.KMCollection_Status_Constant.INDEXSTATUS_OFFLINE()){
    	      vars.$L_file.indexstatus = lib.KMCollection_Status_Constant.INDEXSTATUS_NOTSTARTED();
    	    }
         } else {
    	    vars.$L_file.indexstatus = lib.KMCollection_Status_Constant.INDEXSTATUS_INDEXING();
         }
         vars.$L_file.doSave();
    }
}
else
{
    if (vars.$L_file.searchservername ){
        result = system.library.KMCollection_Update.reindex();
          //print("reindex result: "+result);
          if (result == -1){
                system.library.KMUtils.domessageBox(system.functions.scmsg(224, "km"));
                //system.library.KMUtils.domessageBox("no connection");
                vars.$L_refresh=false;
          }
      }
}
