record.script:
function writeUtfFile(strFileName, strContent)
{
	var result = -1;
   	result = system.functions.plugin("KMPLUGIN","writeUtfFile", strFileName, strContent);
 	return result;
}

function htmlToText(strContent)
{
	var strResult = new Datum();
   	system.functions.plugin("KMPLUGIN","htmlToText", strContent, strResult);
 	return strResult.getText();
}
