record.script:
// testing log4js
// logLevel 1 = DEBUG, 2 = INFO, 3 = WARN, 4 = ERROR, 5 = FATAL;
var logLevel = 4;
var show = new lib.log4js.Log(logLevel,"log_test");
show.fileLogger = function(msg, level){
	print(level + " - " + msg);
	system.functions.log(level + " - " + msg);
}

//I want to see if this function will pick up the global show var
function experiment(){
	show.setLevel(1);	// change the log level
	show.setLogger(show.fileLogger);  // send output to screen and log
	show.setPrefix("from sam.experiment");	// change the message prefix
	show.debug("This is a debug message.");
	show.info("This is an information message.");
	show.warn("This is a warning message.");
	show.error("This is an error message.");
	show.fatal("This is a fatal error message.");
}
