record.javascript.actions:
system.vars.$DMTTableConfirmedList = new Array();
system.vars.$numOfTablesToBeExported=0;

for(var i=0;i<system.functions.lng(system.vars.$deltaMigrationToolTableName);i++){
	var curItem="";
	curItem+=system.vars.$deltaMigrationToolTableName[i]+"$";
	curItem+=system.vars.$deltaMigrationToolTimestampField[i]+"$";
	curItem+=system.vars.$deltaMigrationToolSuggestion[i]+"$";
	curItem+=system.vars.$deltaMigrationToolTimeEstimation[i]+"$";
	if(system.vars.$deltaMigrationToolDecision[i]=="Yes"){
		curItem+="true$";
		system.vars.$numOfTablesToBeExported++;
	}
	else
		curItem+="false$";
	curItem+=system.vars.$deltaMigrationToolComment[i];
	system.vars.$DMTTableConfirmedList=system.functions.insert(system.vars.$DMTTableConfirmedList,0,1,curItem);
}
if(system.vars.$numOfTablesToBeExported==0){
	lib.delta_migration_data_utility.myMessageList_add("error","Not find delta data.");
}

record.javascript.init:
system.vars.$DMTTableCheckList=lib.delta_migration_data_utility.unload_data_pre_check(vars.$unlFilePathName, system.vars.$startTime,system.vars.$endTime);

system.vars.$deltaMigrationToolTableName=new Array();
system.vars.$deltaMigrationToolTimestampField=new Array();
system.vars.$deltaMigrationToolSuggestion=new Array();
system.vars.$deltaMigrationToolTimeEstimation=new Array();
system.vars.$deltaMigrationToolDecision=new Array();
system.vars.$deltaMigrationToolComment=new Array();
for(var i in vars.$DMTTableCheckList){
	deltaMigrationTool_tableCheck=""+vars.$DMTTableCheckList[i];
	system.vars.$deltaMigrationToolTableName.push(system.functions.nullsub(deltaMigrationTool_tableCheck.split("$")[0],""));
	system.vars.$deltaMigrationToolTimestampField.push(system.functions.nullsub(deltaMigrationTool_tableCheck.split("$")[1],""));
	system.vars.$deltaMigrationToolSuggestion.push(system.functions.nullsub(deltaMigrationTool_tableCheck.split("$")[2],""));
	system.vars.$deltaMigrationToolTimeEstimation.push(system.functions.nullsub(deltaMigrationTool_tableCheck.split("$")[3],""));
	system.vars.$deltaMigrationToolComment.push(system.functions.nullsub(deltaMigrationTool_tableCheck.split("$")[5],""));
	
	if(deltaMigrationTool_tableCheck.split("$")[4]=="true" || deltaMigrationTool_tableCheck.split("$")[4]==true)
		system.vars.$deltaMigrationToolDecision.push("Yes");
	else
		system.vars.$deltaMigrationToolDecision.push("No");
}
